import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import App from './App';
import BackgroundEditorPage from './components/BackgroundEditorPage';

import { useNavigate } from 'react-router-dom';

export default function AppRouter() {
  const navigate = useNavigate();
  // These refs will be passed to App for scrolling
  const howRef = React.createRef();
  const featuresRef = React.createRef();

  const handleStartRemoving = () => {
    navigate('/editor');
  };
  const handleHowClick = () => {
    if (howRef.current) howRef.current.scrollIntoView({ behavior: 'smooth' });
  };
  const handleFeaturesClick = () => {
    if (featuresRef.current) featuresRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<App onStartRemoving={handleStartRemoving} onHowClick={handleHowClick} onFeaturesClick={handleFeaturesClick} />} />
        <Route path="/editor" element={<BackgroundEditorPage />} />
      </Routes>
    </BrowserRouter>
  );
}
