import React from 'react';
import './HeroSection.css';
import heroMain from '../assets/hero-main.png';
import heroAccent from '../assets/hero-accent.png';
import StatCard from './StatCard';
import magicSvg from '../assets/magic-wand.svg';

function HeroSection() {
  return (
    <section className="hero-section dark-panel">
      <div className="outer">
        <div className="card hero-card">
          <div className="hero-svg">
            <img src={magicSvg} alt="Magic Wand" style={{width:'54px',height:'54px',marginBottom:'12px'}} />
          </div>
          <h1 className="hero-title">Remove Backgrounds Instantly</h1>
          <p className="hero-desc">Effortlessly erase backgrounds from your photos. Fast, private, and free. Perfect for profile pics, products, and more.</p>
          <a href="/editor" className="hero-btn dark-glass">Get Started</a>
          <div className="hero-images">
            <img src={heroMain} alt="Main hero visual" className="hero-image-main" />
            <img src={heroAccent} alt="Accent design" className="hero-image-accent" />
          </div>
        </div>
      </div>
    </section>
  );
}

export default HeroSection;
