import React from 'react';
import './Features.css';
import feature1 from '../assets/feature1.png';
import feature2 from '../assets/feature2.png';

import StatCard from './StatCard';
import magicWandSvg from '../assets/magic-wand.svg';
import uploadCloudSvg from '../assets/upload-cloud.svg';

function Features() {
  return (
    <section className="features-section" id="features">
      <h2 className="features-title">Why choose openbg?</h2>
      <div className="features-list">
        <StatCard value={<img src={magicWandSvg} alt="Private" style={{width:'48px',height:'48px'}} />} label={<><h3>Private & Secure</h3><p>All processing is local. Your images never leave your device.</p></>} />
        <StatCard value={<img src={uploadCloudSvg} alt="Fast" style={{width:'48px',height:'48px'}} />} label={<><h3>Fast & Free</h3><p>Backgrounds removed in seconds. No sign-up required.</p></>} />
      </div>
    </section>
  );
}

export default Features;
