import Header from './components/Header';
import HeroSection from './components/HeroSection';
import HowThisWorks from './components/HowThisWorks';
import Features from './components/Features';
import DownloadPanel from './components/DownloadPanel';
import Footer from './components/Footer';
import './App.css';



import React, { useRef } from 'react';

import { useNavigate } from 'react-router-dom';

function App() {
  const uploaderRef = useRef(null);
  const howRef = useRef(null);
  const featuresRef = useRef(null);
  const navigate = useNavigate();

  const scrollToUploader = () => {
    navigate('/editor');
  };
  const scrollToHow = () => {
    if (howRef.current) howRef.current.scrollIntoView({ behavior: 'smooth' });
  };
  const scrollToFeatures = () => {
    if (featuresRef.current) featuresRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      <Header onStartRemoving={scrollToUploader} onHowClick={scrollToHow} onFeaturesClick={scrollToFeatures} />
      <HeroSection onGetStarted={scrollToUploader} />
      <div ref={howRef}><HowThisWorks /></div>
      <div ref={featuresRef}><Features /></div>
      <DownloadPanel ref={uploaderRef} />
      <Footer />
    </div>
  );
}



export default App;
