import React from 'react';
import './BackgroundOptions.css';

const solidColors = ['#fff', '#111', '#fbca1f', '#e0e0e0', '#f7b2b7'];
const sampleImages = [
  { name: 'Sky', url: '/sample-bg/sky.jpg' },
  { name: 'Desk', url: '/sample-bg/desk.jpg' },
  { name: 'Wall', url: '/sample-bg/wall.jpg' },
];

function BackgroundOptions({ onSelect }) {
  return (
    <div className="bg-options">
      <div className="bg-options-label">Choose a background:</div>
      <div className="bg-options-row">
        {solidColors.map((color) => (
          <button
            key={color}
            className="bg-color-btn"
            style={{ background: color }}
            onClick={() => onSelect({ type: 'color', value: color })}
          />
        ))}
        {sampleImages.map((img) => (
          <button
            key={img.name}
            className="bg-img-btn"
            onClick={() => onSelect({ type: 'image', value: img.url })}
          >
            <img src={img.url} alt={img.name} />
          </button>
        ))}
        <label className="bg-upload-btn">
          +
          <input type="file" accept="image/*" style={{ display: 'none' }} onChange={e => onSelect({ type: 'user', value: e.target.files[0] })} />
        </label>
      </div>
    </div>
  );
}

export default BackgroundOptions;
