import React, { useRef } from 'react';
import './ImageUploader.css';

function ImageUploader({ onProcess, images, loading }) {
  const inputRef = useRef();

  const handleChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      onProcess(file);
    }
  };

  const handleClick = () => {
    inputRef.current.value = '';
    inputRef.current.click();
  };

  const download = (b64, name) => {
    const link = document.createElement('a');
    link.href = `data:image/png;base64,${b64}`;
    link.download = name;
    link.click();
  };

  return (
    <div className="uploader-container">
      <input
        ref={inputRef}
        type="file"
        accept="image/*"
        style={{ display: 'none' }}
        onChange={handleChange}
      />
      <button className="upload-btn" onClick={handleClick} disabled={loading}>
        {loading ? 'Processing...' : 'Upload Image'}
      </button>
      {images && (
        <div className="results">
          <h3>Preview & Download</h3>
          <div className="image-grid">
            <div className="image-item">
              <img src={`data:image/png;base64,${images.original}`} alt="Original" />
              <button onClick={() => download(images.original, 'bg-removed-original.png')}>Download Original</button>
            </div>
            <div className="image-item">
              <img src={`data:image/png;base64,${images.size_512}`} alt="512x512" width={128} height={128} />
              <button onClick={() => download(images.size_512, 'bg-removed-512.png')}>Download 512x512</button>
            </div>
            <div className="image-item">
              <img src={`data:image/png;base64,${images.size_256}`} alt="256x256" width={64} height={64} />
              <button onClick={() => download(images.size_256, 'bg-removed-256.png')}>Download 256x256</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ImageUploader;
