import Header from './components/Header';
import HeroSection from './components/HeroSection';
import HowThisWorks from './components/HowThisWorks';
import Features from './components/Features';
import DownloadPanel from './components/DownloadPanel';
import Footer from './components/Footer';
import './App.css';



import React, { useRef } from 'react';

function App(props) {
  // Fallback scroll handlers if not provided
  const uploaderRef = useRef(null);
  const howRef = useRef(null);
  const featuresRef = useRef(null);
  const safeOnHowClick = props.onHowClick || (() => { if (howRef.current) howRef.current.scrollIntoView({ behavior: 'smooth' }); });
  const safeOnFeaturesClick = props.onFeaturesClick || (() => { if (featuresRef.current) featuresRef.current.scrollIntoView({ behavior: 'smooth' }); });
  const safeOnStartRemoving = props.onStartRemoving || (() => { if (uploaderRef.current) uploaderRef.current.scrollIntoView({ behavior: 'smooth' }); });

  return (
    <div>
      <Header onStartRemoving={safeOnStartRemoving} onHowClick={safeOnHowClick} onFeaturesClick={safeOnFeaturesClick} />
      <HeroSection onGetStarted={safeOnStartRemoving} />
      <div ref={howRef}><HowThisWorks /></div>
      <div ref={featuresRef}><Features /></div>
      <DownloadPanel ref={uploaderRef} />
      <Footer />
    </div>
  );
}





export default App;
